import React, { useState, useEffect } from "react";
import Papa from "papaparse";


interface MultiplicationGameProps {
  data: any[];
}

const MultiplicationGame: React.FC<MultiplicationGameProps> = () => {
  const [data, setData] = useState<any[]>([]);
  const [num1, setNum1] = useState<number | null>(null);
  const [num2, setNum2] = useState<number | null>(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch("/mathdata.csv") // Se till att mathdata.csv ligger i public/ mappen
      .then((response) => response.text())
      .then((csvText) => {
        const parsed = Papa.parse(csvText, {
          delimiter: ";", // Viktigt! CSV använder semikolon
          header: true,
          dynamicTyping: true,
        });
        setData(parsed.data);
      });
  }, []);

  const getRandomNumbers = () => {
    if (data.length > 1) {
      let index1 = Math.floor(Math.random() * data.length);
      let index2 = Math.floor(Math.random() * data.length);

      // Se till att index1 och index2 är olika så att vi får två olika tal
      while (index1 === index2) {
        index2 = Math.floor(Math.random() * data.length);
      }

      // Hämta num1 och num2 från olika rader
      const newNum1 = data[index1]?.num1 ?? 1;
      const newNum2 = data[index2]?.num2 ?? 1;

      setNum1(newNum1);
      setNum2(newNum2);
    }
  };

  useEffect(() => {
    if (data.length > 1) {
      getRandomNumbers();
    }
  }, [data]);

  const checkAnswer = () => {
    if (num1 === null || num2 === null) return;

    const correctAnswer = num1 * num2;
    if (parseInt(userAnswer) === correctAnswer) {
      setMessage("✅ Rätt svar!");
    } else {
      setMessage(`❌ Fel! Rätt svar var ${correctAnswer}`);
    }

    setUserAnswer("");
    getRandomNumbers(); // Ladda nästa fråga
  };

  return (
    <div className="game-container">
      {num1 !== null && num2 !== null ? (
        <>
          <p>{num1} × {num2} = ?</p>
          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && checkAnswer()} 
          />
          <button onClick={checkAnswer}>Svara</button>
          <p>{message}</p>
        </>
      ) : (
        <p>⏳ Laddar fråga...</p>
      )}
    </div>
  );
};
export default MultiplicationGame;
