import React, { useState, useEffect } from "react";

interface MultiplicationGameProps {
  difficulty: string;
  updateHighScore: (score: number, game: string) => void;
}

const MultiplicationGame: React.FC<MultiplicationGameProps> = ({ difficulty, updateHighScore }) => {
  const [num1, setNum1] = useState(1);
  const [num2, setNum2] = useState(1);
  const [userAnswer, setUserAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [score, setScore] = useState(0);
  const [round, setRound] = useState(1);
  const totalRounds = 10;
  const [gameOver, setGameOver] = useState(false);

  const getRandomNumbers = () => {
    const maxNum = difficulty === "easy" ? 10 : difficulty === "medium" ? 50 : 100;
    setNum1(Math.floor(Math.random() * maxNum) + 1);
    setNum2(Math.floor(Math.random() * maxNum) + 1);
  };

  useEffect(() => {
    getRandomNumbers();
  }, []);

  const checkAnswer = () => {
    if (parseInt(userAnswer) === num1 * num2) {
      setScore((prev) => prev + (difficulty === "easy" ? 1 : difficulty === "medium" ? 3 : 5));
      setMessage("‚úÖ R√§tt svar!");
    } else {
      setMessage(`‚ùå Fel! R√§tt svar var ${num1 * num2}`);
    }

    if (round < totalRounds) {
      setRound((prev) => prev + 1);
      getRandomNumbers();
      setUserAnswer("");
    } else {
      setGameOver(true);
      updateHighScore(score, "Multiplikation");
    }
  };

  const restartGame = () => {
    setScore(0);
    setRound(1);
    setMessage("");
    setGameOver(false);
    getRandomNumbers();
  };

  return (
    <div className="game-container">
      {gameOver ? (
        <div className="game-over">
          <h2>üéÆ Game Over! üéâ</h2>
          <p>Du fick {score} po√§ng!</p>
          <button onClick={restartGame}>üîÑ Spela igen</button>
        </div>
      ) : (
        <>
          <h2>Fr√•ga {round}/{totalRounds}</h2>
          <p>{num1} √ó {num2} = ?</p>
          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && checkAnswer()}
          />
          <button onClick={checkAnswer}>Svara</button>
          <p>{message}</p>
          <p>Po√§ng: {score}</p>
        </>
      )}
    </div>
  );
};

export default MultiplicationGame;
