import React, { useState, useEffect } from "react";

interface MultiplicationGameProps {
  data: any[];
}

const MultiplicationGame: React.FC<MultiplicationGameProps> = ({ data }) => {
  const [num1, setNum1] = useState(1);
  const [num2, setNum2] = useState(1);
  const [userAnswer, setUserAnswer] = useState("");
  const [score, setScore] = useState(0);
  const [round, setRound] = useState(1);
  const [message, setMessage] = useState("");

  const totalRounds = 10;

  const getRandomNumbers = () => {
    if (data.length > 0) {
      const randomIndex = Math.floor(Math.random() * data.length);
      setNum1(data[randomIndex]["num1"]);
      setNum2(data[randomIndex]["num2"]);
    }
  };
  React.useEffect(() => {
    if (data.length > 0 && round === 1) {
      getRandomNumbers();
    }
  }, [data, round]);

  const checkAnswer = () => {
    const correctAnswer = num1 * num2;
    if (parseInt(userAnswer) === correctAnswer) {
      setScore(score + 1);
      setMessage("‚úÖ R√§tt svar!");
    } else {
      setMessage(`‚ùå Fel! R√§tt svar var ${correctAnswer}`);
    }

    if (round < totalRounds) {
      setRound(round + 1);
      setUserAnswer("");
    } else {
      // Spelet √§r slut
      setMessage(`üéâ Spelet √§r slut! Du fick ${score + (parseInt(userAnswer) === correctAnswer ? 1 : 0)} av ${totalRounds} r√§tt.`);
    }
  };

  return (
    <><>
          <div className="mult-container">
              <h1>MULTIM√ÑSTAREN</h1>
          </div>
      </><div className="game-container">
              {round <= totalRounds ? (
                  <>
                      <p>Fr√•ga {round}/{totalRounds}</p>
                      <p>{num1} √ó {num2} = ?</p>
                      <input
                          type="number"
                          value={userAnswer}
                          onChange={(e) => setUserAnswer(e.target.value)}
                          onKeyDown={(e) => e.key === "Enter" && checkAnswer()} />
                      <button onClick={checkAnswer}>Svara</button>
                      <p>{message}</p>
                  </>
              ) : (
                  <h2>{message}</h2>
              )}
          </div></>
  );
};

export default MultiplicationGame;
