import React, { useState, useEffect } from "react";
import Papa from "papaparse";

interface DivisionProps {
  data: any[];
  difficulty: string;
  updateHighScore: (score: number, game: string) => void;
}

const Division: React.FC<DivisionProps> = ({ data }) => {
  const [num1, setNum1] = useState<number | null>(null);
  const [num2, setNum2] = useState<number | null>(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [score, setScore] = useState(0);
  const [round, setRound] = useState(1);
  const totalRounds = 10;
  const [gameOver, setGameOver] = useState(false);

  // Slumpar tv√• tal d√§r num1 √§r en multipel av num2 f√∂r att s√§kerst√§lla att det blir en heltalsdivision
  const getRandomNumbers = () => {
    if (data.length >= 10) {
      let index1 = Math.floor(Math.random() * 10);
      let index2 = Math.floor(Math.random() * 10);

      while (index1 === index2) {
        index2 = Math.floor(Math.random() * 10);
      }

      const divisor = typeof data[index2]?.num2 === "number" ? data[index2].num2 : 1;
      let dividend = typeof data[index1]?.num1 === "number" ? data[index1].num1 : 1;

      // S√§kerst√§lla att dividend √§r en multipel av divisor (f√∂r att f√• heltalsresultat)
      dividend = divisor * Math.floor(Math.random() * 10 + 1);

      setNum1(dividend);
      setNum2(divisor);
    }
  };

  useEffect(() => {
    if (data.length > 1) {
      getRandomNumbers();
    }
  }, [data]);

  const checkAnswer = () => {
    if (num1 === null || num2 === null) return;

    const correctAnswer = num1 / num2;
    if (parseInt(userAnswer) === correctAnswer) {
      setScore((prevScore) => prevScore + 1);
      setMessage("‚úÖ R√§tt svar!");
    } else {
      setMessage(`‚ùå Fel! R√§tt svar var ${correctAnswer}`);
    }

    if (round < totalRounds) {
      setRound((prevRound) => prevRound + 1);
      getRandomNumbers();
      setUserAnswer("");
    } else {
      setMessage(`üéâ Spelet √§r slut! Du fick ${score + (parseInt(userAnswer) === correctAnswer ? 1 : 0)} av ${totalRounds} r√§tt.`);
      setGameOver(true);
    }
  };

  const restartGame = () => {
    setScore(0);
    setRound(1);
    setMessage("");
    setGameOver(false);
    getRandomNumbers();
  };

  return (
    <div className="game-container">
      {gameOver ? (
        <div className="game-over">
          <h2>üéÆ Game Over! üéâ</h2>
          <p>Du fick {score} av {totalRounds} r√§tt.</p>
          <button onClick={restartGame}>üîÑ Spela igen</button>
        </div>
      ) : (
        <>
          <h2>Fr√•ga {round}/{totalRounds}</h2>
          {num1 !== null && num2 !== null ? (
            <p>{num1} √∑ {num2} = ?</p>
          ) : (
            <p>‚è≥ Laddar fr√•ga...</p>
          )}

          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && checkAnswer()} 
          />
          <button onClick={checkAnswer}>Svara</button>
          <p>{message}</p>
          <p>Po√§ng: {score}</p>
        </>
      )}
    </div>
  );
};

export default Division;
