import React, { useState, useEffect } from "react";
import Papa from "papaparse";

interface AdditionProps {
    data: any[];
}

const Addition: React.FC<AdditionProps> = () => {
    const [data, setData] = useState<any[]>([]);
    const [num1, setNum1] = useState<number | null>(null);
    const [num2, setNum2] = useState<number | null>(null);
    const [userAnswer, setUserAnswer] = useState("");
    const [message, setMessage] = useState("");
    const [score, setScore] = useState(0);
    const [round, setRound] = useState(1);
    const totalRounds = 10;
    const [gameOver, setGameOver] = useState(false);

    useEffect(() => {
        fetch("/mathdata.csv")
          .then((response) => response.text())
          .then((csvText) => {
            const parsed = Papa.parse(csvText, {
              delimiter: ";",
              header: true,
              dynamicTyping: true,
            });
            setData(parsed.data);
          });
      }, []);

    const getRandomNumbers = () => {
        if (data.length >= 20) {
            let index1 = Math.floor(Math.random() * 20);
            let index2 = Math.floor(Math.random() * 20);

            // Se till att vi f√•r olika index
            while (index1 === index2) {
                index2 = Math.floor(Math.random() * 20);
            }

            // H√§mtar tal fr√•n CSV-filen
            const newNum1 = typeof data[index1]?.num1 === "number" ? data[index1].num1 : 1;
            const newNum2 = typeof data[index2]?.num1 === "number" ? data[index2].num1 : 1;

            setNum1(newNum1);
            setNum2(newNum2);
        }
    };

    useEffect(() => {
        if (data.length > 1) {
            getRandomNumbers();
        }
    }, [data]);

    const checkAnswer = () => {
        if (num1 === null || num2 === null) return; 

        const correctAnswer = num1 + num2;

        if (parseInt(userAnswer) === correctAnswer) {
            setScore((prevScore) => prevScore + 1);
            setMessage("‚úÖ R√§tt svar!");
        } else {
            setMessage(`‚ùå Fel! R√§tt svar var ${correctAnswer}`);
        }

        if (round < totalRounds) {
            setRound((prevRound) => prevRound + 1);
            getRandomNumbers();
            setUserAnswer("");
        } else {
            setMessage(`üéâ Spelet √§r slut! Du fick ${score + (parseInt(userAnswer) === correctAnswer ? 1 : 0)} av ${totalRounds} r√§tt.`);
            setGameOver(true);
        }
    }

    const restartGame = () => {
        setScore(0);
        setRound(1);
        setMessage("");
        setGameOver(false);
        getRandomNumbers();
    };

    return (
        <div className="game-container">
            {gameOver ? (
                <div className="game-over">
                    <h2>üéÆ Game Over! üéâ</h2>
                    <p>Du fick {score} av {totalRounds} r√§tt.</p>
                    <button onClick={restartGame}>üîÑ Spela igen</button>
                </div>
            ) : (
                <>
                    <h2>Fr√•ga {round}/{totalRounds}</h2>
                    {num1 !== null && num2 !== null ? (
                        <p>{num1} + {num2} = ?</p>
                    ) : (
                        <p>‚è≥ Laddar fr√•ga...</p>
                    )}

                    <input
                        type="number"
                        value={userAnswer}
                        onChange={(e) => setUserAnswer(e.target.value)}
                        onKeyDown={(e) => e.key === "Enter" && checkAnswer()} 
                    />
                    <button onClick={checkAnswer}>Svara</button>
                    <p>{message}</p>
                    <p>Po√§ng: {score}</p>
                </>
            )}
        </div>
    );
};

export default Addition;
