import React, { useState, useEffect } from "react";
import "./App.css";
// import InputField from "./components/InputField";
import MultiplicationGame from "./components/MultiplicationGame";
import useFetch from "./hooks/useFetch";
import TableMultiplication from "./components/TableMultiplication";

const App: React.FC = () => {
  const [option, setOption] = useState<string | null>(null);
  const [data, setData] = useState<any[]>([]);
  const { fetchCsvData } = useFetch();

  const firstValue = data.length > 0 ? data[0]["num1"] : "Laddar...";

  useEffect(() => {
    fetchCsvData('/mathdata.csv', setData);
  }, []);

  console.log(data);
/*

            {data.map((nums, index) => (
            <p key={index}>{nums["num_1-20"]}</p>
          ))}
*/

  return (
    <div className="App">

      {!option ? (
        <>
          <h1 className = "heading">Välkommen till Mattespelet!</h1>
          <h2 className = "subtitle">Vad vill du spela?</h2>

          <p>Första värdet i första kolumnen: {firstValue}</p>

      <div className="menu">
        <button onClick={() => setOption("multiplikation")}>Multiplikation</button>
        <button onClick={() => setOption("tabell")}>Tabell</button>
        <button onClick={() => setOption("addition")}>Addition</button>
        <button onClick={() => setOption("subtraktion")}>Subtraktion</button>
      </div>
      </>
    ) : (
      <>
       <button className="back-button" onClick={() => setOption(null)}>
            ⬅ Tillbaka till menyn
          </button>
        {option === "multiplikation" && <MultiplicationGame data={data}/>}
        {option === "tabell" && <TableMultiplication data={data}/>}
      </>
      )}
    </div>
  );
}

export default App;
