import React, { useState, useEffect } from "react";

interface MultiplicationGameProps {
  data: any[];
}

const TableMultiplication: React.FC<MultiplicationGameProps> = ({ data }) => {
  const [table, setTable] = useState<number | null>(null);
  const [num1, setNum1] = useState<number | null>(null);
  const [num2, setNum2] = useState<number | null>(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [score, setScore] = useState(0);
  const [round, setRound] = useState(1);
  const totalRounds = 10;

  // N√§r anv√§ndaren v√§ljer en tabell, starta spelet
  const startGame = (selectedTable: number) => {
    setTable(selectedTable);
    setRound(1);
    setScore(0);
    setMessage("");
    getRandomNumbers(selectedTable);
  };

  // Slumpar en rad och tar r√§tt tabellkolumn
  const getRandomNumbers = (selectedTable: number) => {
    if (data.length >= 10) {
      const randomIndex = Math.floor(Math.random() * 10); // Begr√§nsa till index 0-9
      const row = data[randomIndex];

      setNum1(row.num1);
      setNum2(row[`table${selectedTable}`]); // H√§mtar r√§tt multiplikationstabell
    }
  };

  const checkAnswer = () => {
    if (num1 === null || num2 === null) return;

    const correctAnswer = num1 * (table ?? 1);
    if (parseInt(userAnswer) === correctAnswer) {
      setScore(score + 1);
      setMessage("‚úÖ R√§tt svar!");
    } else {
      setMessage(`‚ùå Fel! R√§tt svar var ${correctAnswer}`);
    }

    if (round < totalRounds) {
      setRound(round + 1);
      getRandomNumbers(table ?? 1);
      setUserAnswer("");
    } else {
      setMessage(`üéâ Spelet √§r slut! Du fick ${score + (parseInt(userAnswer) === correctAnswer ? 1 : 0)} av ${totalRounds} r√§tt.`);
    }
  };

  return (
    <div className="game-container">
      {!table ? (
        <div>
          <h2>V√§lj en multiplikationstabell:</h2>
          {Array.from({ length: 11 }, (_, num) => (
            <button key={num} onClick={() => startGame(num)}>{num}</button>
            ))}
        </div>
      ) : round <= totalRounds ? (
        <>
          <h2>Fr√•ga {round}/{totalRounds}</h2>
          {num1 !== null && num2 !== null ? (
            <p>{num1} √ó {table} = ?</p>
          ) : (
            <p>‚è≥ Laddar fr√•ga...</p>
          )}

          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && checkAnswer()} 
          />
          <button onClick={checkAnswer}>Svara</button>
          <p>{message}</p>
          <p>Po√§ng: {score}</p>
        </>
      ) : (
        <h2>{message}</h2>
      )}
    </div>
  );
};

export default TableMultiplication;
