import React, { useState, useEffect } from "react";

interface DivisionProps {
  data: any[];
  difficulty: string;
  updateHighScore: (score: number, game: string) => void;
}

const Division: React.FC<DivisionProps> = ({ data, difficulty, updateHighScore }) => {
  const [num1, setNum1] = useState<number | null>(null);
  const [num2, setNum2] = useState<number | null>(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [score, setScore] = useState(0);
  const [round, setRound] = useState(1);
  const totalRounds = 10;
  const [gameOver, setGameOver] = useState(false);

  // Slumpar tv√• tal d√§r num1 √§r en multipel av num2 f√∂r att s√§kerst√§lla att divisionen ger ett heltal
  const getRandomNumbers = () => {
    if (data.length >= 10) {
      let maxNum = difficulty === "easy" ? 10 : difficulty === "medium" ? 50 : 100;

      let divisor = Math.floor(Math.random() * (maxNum / 2)) + 1; // Undviker 0 och sm√• tal
      let dividend = divisor * (Math.floor(Math.random() * 10) + 1); // Skapar en multipel

      setNum1(dividend);
      setNum2(divisor);
    }
  };

  useEffect(() => {
    if (data.length > 1) {
      getRandomNumbers();
    }
  }, [data]);

  const checkAnswer = () => {
    if (num1 === null || num2 === null) return;

    const correctAnswer = num1 / num2;
    if (parseInt(userAnswer) === correctAnswer) {
      let points = difficulty === "easy" ? 1 : difficulty === "medium" ? 3 : 5;
      setScore((prevScore) => prevScore + points);
      setMessage("‚úÖ R√§tt svar!");
    } else {
      setMessage(`‚ùå Fel! R√§tt svar var ${correctAnswer}`);
    }

    if (round < totalRounds) {
      setRound((prevRound) => prevRound + 1);
      getRandomNumbers();
      setUserAnswer("");
    } else {
      setGameOver(true);
      updateHighScore(score, "Division");
    }
  };

  const restartGame = () => {
    setScore(0);
    setRound(1);
    setMessage("");
    setGameOver(false);
    getRandomNumbers();
  };

  return (
    <div className="game-container">
      {gameOver ? (
        <div className="game-over">
          <h2>üéÆ Game Over! üéâ</h2>
          <p>Du fick {score} po√§ng!</p>
          <button onClick={restartGame}>üîÑ Spela igen</button>
        </div>
      ) : (
        <>
          <h2>Fr√•ga {round}/{totalRounds}</h2>
          {num1 !== null && num2 !== null ? (
            <p>{num1} √∑ {num2} = ?</p>
          ) : (
            <p>‚è≥ Laddar fr√•ga...</p>
          )}

          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && checkAnswer()} 
          />
          <button onClick={checkAnswer}>Svara</button>
          <p>{message}</p>
          <p>Po√§ng: {score}</p>
        </>
      )}
    </div>
  );
};

export default Division;
